(function($){				
	jQuery.fn.lightTabs = function(options){

		var createTabs = function(){
			tabs = this;
			i = 0;

			showPage = function(i){
				$(tabs).children(".info").children("div").hide();
				$(tabs).children(".info").children("div").eq(i).show();
				$(tabs).children(".tab").children("input").removeClass("active");
				$(tabs).children(".tab").children("input").eq(i).addClass("active");
			}

			showPage(0);				

			$(tabs).children(".tab").children("input").each(function(index, element){
				$(element).attr("data-page", i);
				i++;                        
			});

			$(tabs).children(".tab").children("input").click(function(){
				showPage(parseInt($(this).attr("data-page")));
			});				
		};		
		return this.each(createTabs);
	};	
})(jQuery);
$(document).ready(function(){
	$(".profile").lightTabs();
});

